SELECT
  COUNT(0)
FROM busapply
WHERE type = #{type}
AND auditstatus = 1 
AND companyId = #{companyid}
 AND id  <>  #{id}

SELECT
  COUNT(0)
FROM busapply
WHERE type = #{type}
AND auditresult = 2 
AND companyId = #{companyid}
 AND id  <>  #{id}

