mapper.update:
update busapply
type = #{type}, files = #{files}, applyAccount = #{applyaccount}, applyUserName = #{applyusername}, applyDate = #{applydate}, auditAccount = #{auditaccount}, auditUserName = #{auditusername}, auditDate = NOW(), auditResult = #{auditresult}, auditStatus = #{auditstatus}, auditOpinion = #{auditopinion}, updateTime = NOW(), version =version +1
WHERE id = #{id} AND version =#{version}
