mapper.update:
UPDATE COMPANY A
A.id=#{id}, A.name=#{name}, A.companyTypeId = #{companytypeid}, A.memberType = #{membertype}, A.level = #{level}, B.name = like concat('%', #{countryName}, '%'), A.provinceId = #{provinceId}, C.name = like concat('%', #{provinceName}, '%'), A.cityId = #{cityId}, D.name = like concat('%', #{cityName}, '%'), A.address = #{address}, A.creditCode = #{creditcode}, A.cerificateAttachment1 = #{cerificateattachment1}, A.cerificateAttachment2 = #{cerificateattachment2}, A.email = #{email}, A.adminAccountId = #{adminaccountid}, A.status = #{status}, A.isSendBl = #{issendbl}, A.isReceive = #{isreceive}, A.isEnableSeal = #{isenableseal}, modifyDate = NOW(), A.modifyUserId = #{modifyuserid}, A.modifyUserName = #{modifyusername}, version = version + 1, Sealfile1 = #{Sealfile1} Sealfile2 = #{Sealfile2}
WHERE id = #{id}

mapper.select:
		SELECT
		  COUNT(0)
		FROM company A
		WHERE
            A.name = #{name}
             AND id  <>  #{id}
